  // PUG
  watch(DEV_PATH.view.watch, compilePug);

  // SCSS
  watch(DEV_PATH.style, (compileStyle));

  // JS
  watch(DEV_PATH.script.watch, series(compileJS, reload));

  // Image
  watch(DEV_PATH.image, compressImage);

  // Fonts
  watch(DEV_PATH.fonts, copyFonts);

  return done();

};


